
public class OperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Arithmetic +,-,*,/,%
		 * Bitwise
		 * Relational
		 * Logical
		 */
	 int m=6 , n=4;
	 n+=m; //int r1 = m+n; same as += use as a shortcut 
	 int r2 = m-n;
	 int r3 = m*n;
	 double r4 = (double)m/n; // only division operator only gives 1 value of 1.5 if use double that 1.5 print 
	 int r5 = m%n; // only print remainder 
	 //System.out.println(r1);
	 System.out.println(n);
	 System.out.println(r2);
	 System.out.println(r3);
	 System.out.println(r4);
	 System.out.println(r5);
	}

}
